
real = ""

sample = "bvwbjplbgvbhsrlpgdmjqwftvncz"



def find_distinct_run(x: str, num_chars: int) -> int:
    for i in range(0, len(x)-num_chars):
        possible_marker = x[i:i+num_chars]
        if len({c for c in possible_marker}) == num_chars:
            return i+num_chars

def find_marker(puz_input: str) -> int:
    return find_distinct_run(puz_input, 4)

def find_message(puz_input: str) -> int:
    return find_distinct_run(puz_input, 14)

print(find_marker(real))

print(find_message(sample))
print(find_message(real))